import React from 'react'
import { Routes, Route, useLocation } from 'react-router-dom'
import { motion, AnimatePresence } from 'framer-motion'
import Navbar from './components/Navbar'
import HeroSection from './components/HeroSection'
import AboutSection from './components/AboutSection'
import ServicesSection from './components/ServicesSection'
import SkillsTabs from './components/SkillsTabs'
import ProjectsCarousel from './components/ProjectsCarousel'
import EducationTimeline from './components/EducationTimeline'
import ContactForm from './components/ContactForm'
import Footer from './components/Footer'
import ParticlesBackground from './components/ParticlesBackground'

function ScrollToHash() {
  const { hash } = useLocation()
  React.useEffect(() => {
    if (hash) {
      const el = document.getElementById(hash.replace('#', ''))
      if (el) el.scrollIntoView({ behavior: 'smooth' })
    }
  }, [hash])
  return null
}

export default function App() {
  const location = useLocation()
  return (
    <div className="min-h-screen bg-gradient-to-b from-[#04121a] via-[#082034] to-[#081b29] text-white">
      <ParticlesBackground />
      <Navbar />
      <ScrollToHash />
      <AnimatePresence mode="wait">
        <Routes location={location} key={location.pathname}>
          <Route path="/" element={
            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
              <main className="container mx-auto px-6 py-24 space-y-28">
                <section id="home"><HeroSection /></section>
                <section id="about"><AboutSection /></section>
                <section id="services"><ServicesSection /></section>
                <section id="skills"><SkillsTabs /></section>
                <section id="projects"><ProjectsCarousel /></section>
                <section id="education"><EducationTimeline /></section>
                <section id="contact"><ContactForm /></section>
              </main>
              <Footer />
            </motion.div>
          } />
        </Routes>
      </AnimatePresence>
    </div>
  )
}
