import React, { useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { skills } from '../data/data'

export default function SkillsTabs(){
  const tabs = ['technical','soft','tools']
  const [active, setActive] = useState('technical')
  return (
    <div>
      <h2 className="text-3xl font-bold mb-6">My <span className="text-teal-300">Skills</span></h2>
      <div className="tab-buttons flex gap-3 mb-4">
        {tabs.map(t=> (
          <button key={t} onClick={()=>setActive(t)} className={`px-4 py-2 rounded ${active===t? 'bg-gradient-to-r from-teal-400 to-purple-600 text-white' : 'bg-white/5'}`}>{t}</button>
        ))}
      </div>
      <div className="tab-content">
        <AnimatePresence mode="wait">
          <motion.div key={active} initial={{ opacity:0, y:10 }} animate={{ opacity:1, y:0 }} exit={{ opacity:0, y:-10 }} transition={{ duration:0.3 }} className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {skills[active].map(s=> (
              <div key={s} className="glass p-4 rounded text-center">{s}</div>
            ))}
          </motion.div>
        </AnimatePresence>
      </div>
    </div>
  )
}
