import React, { useEffect, useState } from 'react'
import { navLinks } from '../data/data'
import { FaMoon, FaSun } from 'react-icons/fa'

export default function Navbar(){
  const [active, setActive] = useState('home')
  const [scrolled, setScrolled] = useState(false)
  const [theme, setTheme] = useState('dark')

  useEffect(()=>{
    const onScroll = () => setScrolled(window.scrollY > 30)
    window.addEventListener('scroll', onScroll)
    const onHashChange = () => setActive(window.location.hash.replace('#','') || 'home')
    window.addEventListener('hashchange', onHashChange)
    return ()=>{ window.removeEventListener('scroll', onScroll); window.removeEventListener('hashchange', onHashChange) }
  },[])

  useEffect(()=>{
    document.documentElement.classList.toggle('dark', theme==='dark')
  },[theme])

  const handleClick = (id) => {
    setActive(id)
    const el = document.getElementById(id)
    if(el) el.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <header className={`fixed w-full z-30 top-0 transition-all ${scrolled? 'backdrop-blur-md bg-black/30':'bg-transparent'}`}>
      <div className="container mx-auto px-6 py-4 flex items-center justify-between">
        <a href="#home" className="text-2xl font-bold">Portfolio</a>
        <nav className="hidden md:flex gap-6 items-center">
          {navLinks.map(n => (
            <button key={n.id} onClick={()=>handleClick(n.id)} className={`px-3 py-1 rounded ${active===n.id ? 'text-teal-300' : 'text-white/80'}`}>
              {n.label}
            </button>
          ))}
          <button onClick={()=>setTheme(t=>t==='dark'?'light':'dark')} className="p-2 rounded bg-white/5">
            {theme==='dark' ? <FaSun /> : <FaMoon />}
          </button>
        </nav>
        <div className="md:hidden">
          <select onChange={(e)=>handleClick(e.target.value)} className="bg-white/5 p-2 rounded">
            {navLinks.map(n=> <option key={n.id} value={n.id}>{n.label}</option>)}
          </select>
        </div>
      </div>
    </header>
  )
}
