import React, { useState } from 'react'
import { motion } from 'framer-motion'

export default function ContactForm(){
  const [submitted, setSubmitted] = useState(false)

  return (
    <div className="max-w-2xl">
      <h2 className="text-3xl font-bold mb-6">Contact <span className="text-teal-300">Me</span></h2>
      {!submitted ? (
        <motion.form action="https://api.web3forms.com/submit" method="POST" initial={{ opacity:0, y:10 }} animate={{ opacity:1, y:0 }} className="grid gap-3">
          <input type="hidden" name="access_key" value="063d6b6d-a53a-4930-b412-b820609a6085" />
          <input className="p-3 glass rounded" type="text" name="name" placeholder="Your Name" required />
          <input className="p-3 glass rounded" type="email" name="email" placeholder="Your Email" required />
          <textarea className="p-3 glass rounded" name="message" rows="5" placeholder="Your Message" required />
          <div className="flex gap-3">
            <button className="px-4 py-2 rounded bg-gradient-to-r from-teal-400 to-purple-600" type="submit">Send Message</button>
            <button type="button" className="px-4 py-2 rounded bg-white/5" onClick={()=>setSubmitted(true)}>Preview</button>
          </div>
        </motion.form>
      ) : (
        <div className="glass p-6 rounded">Thank you! (Preview mode)</div>
      )}
    </div>
  )
}
